CREATE TABLE "doctors" (
    "idDoctors" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "doc_name" VARCHAR(45),
    "doc_secname" VARCHAR(45),
    "doc_surname" VARCHAR(45),
    "doc_rules" INTEGER
);

CREATE TABLE "patients" (
    "idPatients" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "Name" VARCHAR(145) NOT NULL,
    "Secname" VARCHAR(145),
    "Surname" VARCHAR(145) NOT NULL,
    "Birthdate" DATE,
    "Username" VARCHAR(145) NOT NULL,
    "Password" VARCHAR(45) NOT NULL
);

CREATE TABLE "videos" (
    "idvideos" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "video_name" VARCHAR(45),
    "filename" VARCHAR(45),
    "ex_type" VARCHAR(50) NOT NULL DEFAULT 'для всех'
);

CREATE TABLE "disorders" (
    "idDisorders" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "Disorder_name" VARCHAR(45),
    "Disorder_type" VARCHAR(45)
);

CREATE TABLE "appointments" (
    "idAppointments" INTEGER GENERATED BY DEFAULT AS IDENTITY,
    "Starttime" DATE,
    "Endtime" DATE,
    "Comments" TEXT,
    "idPatients" INTEGER NOT NULL,
    "idvideos" INTEGER NOT NULL,
    "idDoctors" INTEGER NOT NULL DEFAULT 1,
    "kolvden" INTEGER NOT NULL DEFAULT 1,
    "dlitelnost" TIME WITHOUT TIME ZONE DEFAULT '00:00:50'::time,
    "sdelanovden" INTEGER NOT NULL DEFAULT 0,
    "sdelanovsego" INTEGER NOT NULL DEFAULT 0,
    "Lastsession" TIMESTAMP WITHOUT TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    "done_percent" INTEGER NOT NULL DEFAULT 0,
    "CumulativeTimeSpent" DOUBLE PRECISION DEFAULT 0,
    PRIMARY KEY ("idAppointments", "idPatients", "idDoctors"),
    FOREIGN KEY ("idDoctors") REFERENCES "doctors" ("idDoctors"),
    FOREIGN KEY ("idPatients") REFERENCES "patients" ("idPatients"),
    FOREIGN KEY ("idvideos") REFERENCES "videos" ("idvideos")
);

CREATE TABLE "patient_disorders" (
    "patient_id" INTEGER NOT NULL,
    "disorder_id" INTEGER NOT NULL,
    PRIMARY KEY ("patient_id", "disorder_id"),
    FOREIGN KEY ("patient_id") REFERENCES "patients" ("idPatients"),
    FOREIGN KEY ("disorder_id") REFERENCES "disorders" ("idDisorders")
);

CREATE TABLE "disorder_videos" (
    "disorder_id" INTEGER NOT NULL,
    "videos_id" INTEGER NOT NULL,
    PRIMARY KEY ("disorder_id", "videos_id"),
    FOREIGN KEY ("disorder_id") REFERENCES "disorders" ("idDisorders") ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY ("videos_id") REFERENCES "videos" ("idvideos")
);

CREATE TABLE "push_count" (
    "id" INTEGER PRIMARY KEY,
    "count" INTEGER
);

CREATE TABLE "rules" (
    "doc_rules" INTEGER PRIMARY KEY,
    "rule_description" VARCHAR(45)
);

CREATE INDEX "ix_appointments_idPatients" ON "appointments" ("idPatients");
CREATE INDEX "ix_appointments_idvideos" ON "appointments" ("idvideos");
CREATE INDEX "ix_appointments_idDoctors" ON "appointments" ("idDoctors");
CREATE INDEX "ix_disorder_videos_videos_id" ON "disorder_videos" ("videos_id");
CREATE INDEX "ix_patient_disorders_disorder_id" ON "patient_disorders" ("disorder_id");











CREATE OR REPLACE FUNCTION reset_field_event() RETURNS void LANGUAGE plpgsql AS $$
BEGIN
    UPDATE "appointments" SET "sdelanovden" = 0;
    UPDATE "appointments" SET "CumulativeTimeSpent" = 0;
END;
$$;

CREATE OR REPLACE FUNCTION update_done_percent_event() RETURNS void LANGUAGE plpgsql AS $$
BEGIN
    UPDATE "appointments"
    SET "done_percent" = CASE
        WHEN ((CURRENT_DATE - "Starttime") * "kolvden") > 0 THEN
            ("sdelanovsego" * 100) / ((CURRENT_DATE - "Starttime") * "kolvden")
        ELSE 0
    END
    WHERE CURRENT_DATE BETWEEN "Starttime" AND "Endtime";
END;
$$;

COMMENT ON FUNCTION reset_field_event IS 'Call daily (for example via cron) to reset per-day counters.';
COMMENT ON FUNCTION update_done_percent_event IS 'Call daily (for example via cron) to refresh done_percent similar to the MySQL event.';

